import React from 'react'
import {NavLink, Link, withRouter, matchPath} from 'react-router-dom';

const $ = window.$;

class SideNavContent extends React.Component {

	constructor(props) {
		super(props)
		this._id = 'SideNavContent'
	}

	componentDidMount() {
		$('.ui.accordion').accordion()
	}

	isActiveUrl = (path, match, location) => {
        return 
		if(path && location.pathname.startsWith(path)) {
			return true
		}
		if(!match) {
			return false
		}
		return true
	}
	render() {
        const {routes, children} = this.props

		return(
			<React.Fragment>
				<div className="ui secondary vertical menu accordion " id="kNav">
					{routes.map((mainNav, index) => {
						let isActive = this.props.location.pathname.startsWith(mainNav.rootPath)
						let activeClass = isActive ? 'active': ''
						if (!mainNav.title) return;
						if (typeof mainNav.accessible !== 'undefined' && !mainNav.accessible) return;
						if(mainNav.nav == false) return
						return (
							<div key={index}>
								{
									mainNav.title && <NavLink exact to={mainNav.path}  isActive={this.isActiveUrl.bind(this, mainNav.rootPath)} className="title item" style={{paddingLeft: (mainNav.icon ? "auto": '15px')}} activeStyle={{color: '#5d2e8e', fontWeight: 'bold'}}>
									{mainNav.icon ? mainNav.icon : null}
									<span className="mg-top-5">{mainNav.title}</span>
									{mainNav.rootPath && !!mainNav.routes && <i className="dropdown icon float-right"></i>}
									</NavLink>
								}
								{
									mainNav.routes && (
										<div style={{paddingLeft: '20px'}} className={'content ' + activeClass}>
											{
												mainNav.routes.map((nav, ndex) => {
													if (!nav.title) return null;
													let showNav = typeof nav.accessible === 'undefined' || nav.accessible;
													if (showNav) {
														return (
															<div key={ndex}>
															<NavLink className='item'  to={nav.path}>
																{nav.icon}
																{nav.title}
															</NavLink>
															</div>
														)
													}
												})
											}
										</div>
									)
								}
							</div>
						)
					})}
				</div>
			</React.Fragment>
		)
	}
}
export {SideNavContent}
